(function($) {
    "use strict";

    // Toggle the sidebar
    $("#sidebarToggle, #sidebarToggleTop").on("click", function(e) {
        $("body").toggleClass("sidebar-toggled");
        $(".sidebar").toggleClass("toggled");
        
        // Hide the sidebar collapse when toggled
        if ($(".sidebar").hasClass("toggled")) {
            $(".sidebar .collapse").collapse("hide");
            
            // Hide the text of side navigation items
            $(".sidebar-text").addClass("hidden");
        } else {
            // Show the text of side navigation items
            $(".sidebar-text").removeClass("hidden");
        }
    });

    // Collapse the sidebar on window resize
    $(window).resize(function() {
        if ($(window).width() < 768) {
            $(".sidebar .collapse").collapse("hide");
        }
        if ($(window).width() < 480 && !$(".sidebar").hasClass("toggled")) {
            $("body").addClass("sidebar-toggled");
            $(".sidebar").addClass("toggled");
            $(".sidebar .collapse").collapse("hide");
        }
    });

    // Prevent scrolling on fixed-nav sidebar
    $("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function(e) {
        var delta;
        if ($(window).width() > 768) {
            e.preventDefault();
            delta = e.originalEvent.wheelDelta || -e.originalEvent.detail;
            this.scrollTop += (delta < 0 ? 1 : -1) * 30;
        }
    });

    // Show or hide the scroll-to-top button based on scroll position
    $(document).on("scroll", function() {
        if ($(this).scrollTop() > 100) {
            $(".scroll-to-top").fadeIn();
        } else {
            $(".scroll-to-top").fadeOut();
        }
    });

    // Smooth scrolling when clicking on scroll-to-top link
    $(document).on("click", "a.scroll-to-top", function(e) {
        var $anchor = $(this);
        $("html, body").stop().animate({
            scrollTop: $($anchor.attr("href")).offset().top
        }, 1000, "easeInOutExpo");
        e.preventDefault();
    });

    // Add sidebar toggle functionality
    $('#sidebarToggle').on('click', function() {
        $('.sidebar').toggleClass('toggled');
    });

})(jQuery);
